$.fn.useWord=function(s){var t=$.extend({word:"first"},s);var a=this.text().trim().split(" ");switch(t.word){case"first":{var r=a.shift();this.html((a.length>0?"<span class='useWord-first'>"+r+"</span> ":r)+a.join(" "))}break;case"last":{var i=a.pop();this.html(a.join(" ")+(a.length>0?" <span class='useWord-last'>"+i+"</span>":i))}break}};